import React, { useEffect, useState } from "react";
import { Container, Grid, TextField, Button, Box, Typography, Divider } from "@mui/material";
import axios from "axios";
import { VenomConnect } from "venom-connect";
import { StandaloneProvider } from "everscale-inpage-provider";

const App: React.FC = () => {
  const [githubToken, setGithubToken] = useState("");
  const [userId, setUserId] = useState("");
  const [githubIssue, setGithubIssue] = useState("");
  const [venomConnect, setVenomConnect] = useState<VenomConnect | null>(null);
  const [venomProvider, setVenomProvider] = useState<StandaloneProvider | null>(null);
  const [address, setAddress] = useState<string | undefined>();
  const [balance, setBalance] = useState<string | undefined>();

  const onConnect = async () => {
    if (venomConnect) {
      try {
        const { address } = await venomConnect.getAccount();
        setAddress(address);
        const balance = await venomProvider?.getBalance(address);
        setBalance(balance);
      } catch (e) {
        console.error("Error connecting to wallet: ", e);
      }
    }
  };

  const connectWallet = async () => {
    const venom = new VenomConnect();
    setVenomConnect(venom);
    const standaloneProvider = venom.getProvider();
    setVenomProvider(standaloneProvider);
    try {
      await venom.connect();
    } catch (err) {
      console.log("Failed to connect wallet");
    }
  };

  useEffect(() => {
    const off = venomConnect?.on("connect", onConnect);
    return () => {
      off?.();
    };
  }, [venomConnect, onConnect]);

  return (
    <Container>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Typography variant="h4" component="h1">
            DApp React App
          </Typography>
          <Divider />
        </Grid>

        <Grid item xs={12}>
          <Button variant="contained" color="primary" onClick={connectWallet}>
            Connect Wallet
          </Button>
        </Grid>

        <Grid item xs={12}>
          {address && (
            <Typography variant="h6" component="span">
              Address: {address}
            </Typography>
          )}
          {balance && (
            <Typography variant="h6" component="span">
              Balance: {balance}
            </Typography>
          )}
        </Grid>
      </Grid>
    </Container>
  );
};

export default App;
