import React, { useState } from 'react';
import { VenomConnect } from 'venom-connect';
import { Button, Modal, Form } from 'react-bootstrap';

const App = () => {
  const [venomConnect, setVenomConnect] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [issueUrl, setIssueUrl] = useState('');
  const [name, setName] = useState('');

  const handleConnect = async () => {
    const venom = new VenomConnect({
      theme: 'dark',
      checkNetworkId: 1000,
    });
    await venom.connect();
    setVenomConnect(venom);
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSubmit = async () => {
    const response = await fetch('https://fastapi.blackflower-fbde21e7.westus2.azurecontainerapps.io/start', {
      method: 'POST',
      headers: {
        'access_token': '3e9bd24a88d140c29926d8c96453a39b',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        'user_id': name,
        'url_input': issueUrl,
        'task_index': 0
      })
    });
    const data = await response.json();
    console.log(data);
    setShowModal(false);
  };

  return (
    <div>
      <Button onClick={handleConnect}>Connect Wallet</Button>
      {venomConnect && (
        <Button onClick={handleOpenModal}>Submit Issue</Button>
      )}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Submit Issue</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control type="text" placeholder="Enter name" value={name} onChange={(e) => setName(e.target.value)} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>GitHub Issue URL</Form.Label>
              <Form.Control type="text" placeholder="Enter issue URL" value={issueUrl} onChange={(e) => setIssueUrl(e.target.value)} />
            </Form.Group>
            <Button variant="primary" onClick={handleSubmit}>
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default App;
